import React from 'react';
import '../assets/scss/main.scss';
import Button from './Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { IconDefinition } from '@fortawesome/fontawesome-svg-core';
import { faUser, faCalendar } from '@fortawesome/free-solid-svg-icons';


interface iNewsProps {
    title: string;
    subtitle: string;
    author: string;
    body: string;
    date: string;
    imageUrl: string;
    collapsed: boolean;
}

interface iNewsState {
    collapsed: boolean;
}

class News extends React.Component<iNewsProps, iNewsState> {

    constructor(props: iNewsProps) {
        super(props);
        this.state = { collapsed: this.props.collapsed }
    }

    public toggleNews = () => {
        this.setState({
            collapsed: !!!this.state.collapsed
        })
    }

    public render() {
        return (
            <div className="card" id="newsCard">
                <img className="card-img-top" width={50} height={150} src={this.props.imageUrl} />
                <div className="card-body">
                    <h5 className="card-title">{this.props.title}</h5>
                    <p className="card-text">{this.props.subtitle}</p>
                </div>
                <div className="card-footer">
                    <small className="text-muted" id="bottomPart">
                        <div >
                            <FontAwesomeIcon icon={faUser} />
                            {
                                '  ' + this.props.author
                            }
                        </div>
                        <div>
                            <FontAwesomeIcon icon={faCalendar} />
                            {
                                '  ' + this.props.date
                            }
                        </div>

                    </small>
                </div>

                <button type="button" className="btn btn-primary" data-toggle="modal" data-target=".bd-example-modal-sm">Small modal</button>

                <div className="modal fade bd-example-modal-sm" role="dialog" aria-labelledby="mySmallModalLabel" aria-hidden="true">
                    <div className="modal-dialog modal-sm">
                        <div className="modal-content">
                            ...
                        </div>
                    </div>
                </div>

            </div>
        );
    }
}

const css = `#newsCard{width: 100%; heigth: 100%;}`;
const css2 = `#bottomPart{display: flex; justify-content: space-between;}`;
document.head.appendChild(document.createElement("style")).textContent = css;
document.head.appendChild(document.createElement("style")).textContent = css2;

export default News;
