      this.props.onRotateClick(false);
    }

    return (
      <div id='enveloped' className="container-xxl">
        <div id="leftArrow" onClick={onClickLeft}>
          &#60;
        </div>
        <div id="newsCard">
          {newsList[this.props.index]}
        </div>
        <div id="rightArrow" onClick={onClickRight}>
          &#62;
        </div>
      </div>
    );
  }
}

